INSERT INTO `funitem` VALUES ('20180820170556000497', '2017-07-14 09:00:00', null, '有趣', '001', 'http://localhost/img/avatar.jpg', '知乎', '学校', 'text', '有个哥们儿是奇葩，记得初中上学那会，中午午休的时候那哥们在厕所抽烟。哥们儿还有最后一口，他猛嘬了一下，突然教导主任进来了。看见我俩靠着窗户，问到：“你俩干嘛呢？”我惊慌了转头看向我的哥们，他的表现我至今难忘！他从鼻子里冒出了好多烟，说到：“我在生气。”', '1', '0', '0', '0');
