INSERT INTO `funitem` VALUES ('20180820170556000943', '2017-10-20 09:00:00', null, '有趣', '001', 'http://localhost/img/avatar.jpg', '知乎', '其他', 'text', '地铁上有两个挨着的空座，我上去坐了一个，另一个被一对情侣中的女生坐了。结果女生拉着男朋友的手说:“我本来想让你坐我旁边的…”男生说:“乖，等她下去我就和你一起坐”OK，我非得坐到你俩先下车不可。', '1', '0', '0', '0');
