INSERT INTO `funitem` VALUES ('20180820170556000954', '2017-10-20 09:00:00', null, '有趣', '001', 'http://localhost/img/avatar.jpg', '知乎', '其他', 'text', '大三暑假，送室友坐高铁，临上车前，我说“我去买几个橘子，你就站在此地，不要走动。”<br>室友愣了一下，说：“你他妈什么时候都不忘占我便宜。”', '1', '0', '0', '0');
