INSERT INTO `funitem` VALUES ('20180820170556000962', '2017-10-20 09:00:00', null, '有趣', '001', 'http://localhost/img/avatar.jpg', '知乎', '其他', 'text', '老师这个职业吧，说的形象点就是每天带着学生在知识的海洋里畅游。畅游一段时间吧，你会发现：只有你一个人上岸了！！然后你还得返回，一个一个去；捞。有些吧，昨天捞上来，今天又掉下去了还得捞。在你喘息的时候，你会惊恐的发现：还！有！往！回！游！的！', '1', '0', '0', '0');
