INSERT INTO `funitem` VALUES ('20180820170556000970', '2017-10-20 09:00:00', null, '有趣', '001', 'http://localhost/img/avatar.jpg', '知乎', '其他', 'text', '一对母女的对话：<br>你都这么大了，怎么还找不到对象？<br>妈，别急，总有一个人在等我…..<br>阎王爷是吧', '1', '0', '0', '0');
INSERT INTO `funitem` VALUES ('20180820170556000972', '2017-10-29 09:00:00', null, '有趣', '001', 'http://localhost/img/avatar.jpg', '知乎', '其他', 'text', '刚开学，新老师推门而入，一巴掌拍在讲台上，冷眼看着我们，说：“我给你们说，我这个人从来不讲天理。”<br>班上的气氛一下变得有点凝重，过了一会，他表情一遍，说：“因为我教地理。”', '1', '0', '0', '0');
INSERT INTO `funitem` VALUES ('20180820170556000974', '2017-10-29 09:00:00', null, '有趣', '001', 'http://localhost/img/avatar.jpg', '知乎', '其他', 'text', '化学老师：“你过来闻一下”<br>我：“好刺鼻啊”<br>化学老师：“嗯，这个液体有毒，以后不要闻了”', '1', '0', '0', '0');
INSERT INTO `funitem` VALUES ('20180820170556000976', '2017-10-29 09:00:00', null, '有趣', '001', 'http://localhost/img/avatar.jpg', '知乎', '其他', 'text', '历史老师：“我们来理一下抗日战争。首先是九一八事变，然后发生了什么？”<br>全班思考状，历史老师紧接着说道：“当然是陆振华带着依萍如萍从东北来到上海，遇到何书桓，然后有了情深深雨蒙蒙。”', '1', '0', '0', '0');
